mapper.select:
SELECT *
FROM Rewards
WHERE punchCardId = #{recordId} and userOpenId = #{openId} and rewardType = #{type}

SELECT *
FROM Rewards
WHERE punchCardId = #{recordId} and giveRewardUserId = #{giveRewardUserId} and rewardType = #{type}

SELECT
*
FROM Rewards
WHERE 1 = 1

    and activityId = #{activityId}


    and punchCardId = #{recordId}


    and userOpenId = #{openId}


    and giveRewardUserId = #{giveRewardUserId}


    and rewardType = #{type}

order by createdAt desc

    LIMIT #{startRow}, #{pageSize}

SELECT count(*)
FROM Rewards
WHERE 1 = 1


    and activityId = #{activityId}


    and punchCardId = #{recordId}


    and userOpenId = #{openId}


    and giveRewardUserId = #{giveRewardUserId}


    and rewardType = #{type}

SELECT
    COALESCE(sum(rewardPoint),0)
FROM
    Rewards
WHERE 1 = 1

    and activityId = #{activityId}


    and punchCardId = #{recordId}


    and userOpenId = #{openId}


    and giveRewardUserId = #{giveRewardUserId}


    and rewardType = #{type}

SELECT
    count(*)
from
(
    SELECT
        COALESCE(sum(rewardPoint),0) as sumRewardPoints, userOpenId
    FROM
        Rewards
    WHERE 1 = 1

        and activityId = #{activityId}


        and punchCardId = #{recordId}


        and userOpenId = #{openId}


        and giveRewardUserId = #{giveRewardUserId}


        and rewardType = #{type}

    GROUP BY userOpenId
) a
    where a.sumRewardPoints  <=  #{sumRewardPoints}
